mapper.update:
 UPDATE function_module SET updated = now() 

		, module_name = #{moduleName}


		, url = #{url}


		, remark = #{remark}


		, status = #{status}


		, created = #{created}

 WHERE 1 = 1 AND id = #{id}

mapper.select:
select

from function_module

	1 = 1
	
		 and id = #{id} 
	
	
		, module_name = #{moduleName}
	
	
		, url = #{url}
	
	
		, remark = #{remark}
	
	
		, status = #{status}
	
	
		, created = #{created}

SELECT count(1) FROM function_module

	1 = 1
	
		 and id = #{id} 
	
	
		, module_name = #{moduleName}
	
	
		, url = #{url}
	
	
		, span = #{span}
	
	
		, remark = #{remark}
	
	
		, status = #{status}
	
	
		, created = #{created}
